function App() {
  const [filters, setFilters] = useState({
    section: '',
    code: '',
    service: '',
    price: ''
  });
  
  const [selectedItem, setSelectedItem] = useState<PriceItem | null>(null);

  const filteredData = useMemo(() => {
    return initialData.filter(item => {
      return (
        item.section.toLowerCase().includes(filters.section.toLowerCase()) &&
        item.code.toLowerCase().includes(filters.code.toLowerCase()) &&
        item.service.toLowerCase().includes(filters.service.toLowerCase()) &&
        item.price.toString().includes(filters.price)
      );
    });
  }, [filters]);

  const handleFilterChange = (column: keyof typeof filters, value: string) => {
    setFilters(prev => ({
      ...prev,
      [column]: value
    }));
  };

  return (
    <div 
      className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-blue-50 py-12 px-4 sm:px-6 lg:px-8"
      style={{
        backgroundImage: `url("data:image/svg+xml,%3Csvg width='60' height='60' viewBox='0 0 60 60' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='none' fill-rule='evenodd'%3E%3Cg fill='%234299e1' fill-opacity='0.05'%3E%3Cpath d='M36 34v-4h-2v4h-4v2h4v4h2v-4h4v-2h-4zm0-30V0h-2v4h-4v2h4v4h2V6h4V4h-4zM6 34v-4H4v4H0v2h4v4h2v-4h4v-2H6zM6 4V0H4v4H0v2h4v4h2V6h4V4H6z'/%3E%3C/g%3E%3C/g%3E%3C/svg%3E")`,
      }}
    >
      <div className="max-w-7xl mx-auto">
        <div className="text-center mb-12">
          <div className="flex justify-center mb-6">
            <div className="bg-white p-4 rounded-full shadow-lg">
              <Stethoscope className="h-16 w-16 text-blue-600" />
            </div>
          </div>
          <h1 className="text-4xl font-bold text-blue-900 mb-3">Прейскурант медицинских услуг</h1>
          <p className="text-blue-600 text-lg">Актуальные цены на все виды медицинских услуг</p>
        </div>

        <div className="bg-white rounded-xl shadow-xl overflow-hidden border border-blue-100">
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-blue-200">
              <thead className="bg-gradient-to-r from-blue-50 to-blue-100">
                <tr>
                  {Object.keys(filters).map((column) => (
                    <th key={column} className="px-6 py-4 text-left">
                      <div className="flex flex-col gap-2">
                        <span className="text-sm font-medium text-blue-700 uppercase tracking-wider">
                          {column === 'section' ? 'Раздел' :
                           column === 'code' ? 'Код услуги' :
                           column === 'service' ? 'Наименование услуги' :
                           'Стоимость, руб.'}
                        </span>
                        <div className="relative">
                          <input
                            type="text"
                            className="block w-full rounded-md border-blue-200 shadow-sm focus:border-blue-500 focus:ring-blue-500 sm:text-sm pl-8"
                            placeholder="Фильтр..."
                            value={filters[column as keyof typeof filters]}
                            onChange={(e) => handleFilterChange(column as keyof typeof filters, e.target.value)}
                          />
                          <Search className="absolute left-2 top-1/2 -translate-y-1/2 h-4 w-4 text-blue-400" />
                        </div>
                      </div>
                    </th>
                  ))}
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-blue-100">
                {filteredData.map((item) => (
                  <tr 
                    key={item.id} 
                    className="hover:bg-blue-50 cursor-pointer transition-colors duration-150"
                    onClick={() => setSelectedItem(item)}
                  >
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-blue-900">{item.section}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-blue-900">{item.code}</td>
                    <td className="px-6 py-4 text-sm text-blue-900">{item.service}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-green-600">{item.price} ₽</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>

      {selectedItem && (
        <Modal
          item={selectedItem}
          onClose={() => setSelectedItem(null)}
        />
      )}
    </div>
  );
}

